import { ChangeEvent, useState } from 'react';
import '../CSS/PageCreateProtokoll.css';
import { ProtokollResource } from '../Resources';

export default function CreatePageProtokoll(){
    const [protokoll, setProtokoll] = useState<ProtokollResource>({
        patient: '',
        datum: '',
        public: false,
        closed: false,
        ersteller: '',
        gesamtMenge: 0,
    });

    function update(e: ChangeEvent<HTMLInputElement | HTMLSelectElement>){
        let finalValue;
        if(e.target.value === "true"){
            finalValue = true;
        }else if(e.target.value === "false"){
            finalValue = false;
        }

        if(finalValue){
            setProtokoll({...protokoll, [e.target.name]: finalValue});
        }

        setProtokoll({...protokoll, [e.target.name]: e.target.value});
    }


    return(
        <>
        <h1>Protokoll Erstellen</h1>
        <form >
            <label className="form-label">
                Patient:
                <input type="text" name="patient" value={protokoll.patient}  onChange={update} />
            </label>
            <label className="form-label">
                Datum:
                <input type="date" name="datum" value={protokoll.datum}  onChange={update}/>
            </label>
            <label className="form-label">
                    Public:
                    <select value={protokoll.public === true ? "true" : "false"} onChange={update}>
                        <option value="-" disabled selected>-</option>
                        <option value="true">Ja</option>
                        <option value="false">Nein</option>
                   </select>
            </label>
            <label className="form-label" >
                    Closed:
                    <select >
                        <option value="-" disabled selected>-</option>
                        <option value="true">Ja</option>
                        <option value="false">Nein</option>
                   </select>
            </label>
            <label className="form-label">
                Ersteller:
                <input type="text" name="Name"  />
            </label>
            <label className="form-label">
                Menge:
                <input type="number" name="Menge"  />
            </label>
        </form>
        </>
    )
}
